    public ActionForward addSubmit(ActionMapping mapping, ActionForm form, HttpServletRequest request,
            HttpServletResponse response) {
        LPInfoForm myForm = (LPInfoForm) form;
        String lpNo = myForm.getLpNo();

        try {
        	HibernateSessionFactory.getSession().clear();
        	HibernateSessionFactory.getSession().getTransaction().begin();

        	Lpinfo lpinfo = new Lpinfo();
        	BeanUtils.copyProperties(lpinfo,myForm);
        	lpinfo.setLpNo(lpNo);

        	
        	if (null== myForm.getNextEffectiveDate() || (null!= myForm.getNextEffectiveDate() && myForm.getNextEffectiveDate().length() == 0 ))
        		lpinfo.setNextEffectiveDate(null);
        	
        	
        	
        	//設定客戶代碼
        	String custNo = myForm.getCustNo();
        	Customer customer = customerService.findById(custNo);
        	lpinfo.setCustomer(customer);
        	
        	//設定印表機類型
        	Integer codePrint = myForm.getCodePrinter();
        	Code codeByCodePrinter = codeService.findById(codePrint);
        	lpinfo.setCodeByCodePrinter(codeByCodePrinter);
        	
        	//設定 列印方式
        	Integer codePrintType = myForm.getCodePrintType();
        	Code codeByCodePrintType = codeService.findById(codePrintType);
        	lpinfo.setCodeByCodePrintType(codeByCodePrintType);
        	
        	//設定NEXT印表機類型
        	Integer nextCodePrint = myForm.getNextCodePrinter();
        	Code codeByNextCodePrinter = codeService.findById(nextCodePrint);
        	lpinfo.setCodeByNextCodePrinter(codeByNextCodePrinter);
        	
        	//設定NEXT列印方式
        	Integer nextCodePrintType = myForm.getNextCodePrintType();
        	if(nextCodePrintType != null){
        	   Code codeByNextCodePrintType = codeService.findById(nextCodePrintType);
        	   lpinfo.setCodeByNextCodePrintType(codeByNextCodePrintType);
        	}

        	
        	lpinfoService.save(lpinfo);
            HibernateSessionFactory.getSession().getTransaction().commit();
            request.setAttribute("message", "新增成功");
            return mapping.findForward("message");
        } catch (Exception e) {
            log.error("", e);
            HibernateSessionFactory.getSession().getTransaction().rollback();
			request.setAttribute("message", "系統失敗 ");
			return mapping.findForward("message");
        }

    }    
    
    public ActionForward list(ActionMapping mapping, ActionForm form, HttpServletRequest request,
            HttpServletResponse response) {
    	ActionMessages messages = new ActionMessages();
        LPInfoForm myForm = (LPInfoForm) form;

        try {
        	ValueList dataList = getValueList(request, myForm,"lpinfoList");
            request.setAttribute("dataList", dataList);
            
            String idList = "";
        	for(Object obj : dataList.getList()){
            	idList += BeanUtils.getProperty(obj, "lp_no") + ",";
            }
            request.setAttribute("idList", idList);
            
            String backToListURL = request.getRequestURI() +"?" + request.getQueryString();
            //request.getSession().setAttribute("backToListURL", backToListURL);
            request.setAttribute("backToListURL", URLEncoder.encode(backToListURL, "UTF-8"));

        } catch (Exception e) {
            log.error("", e);
            messages.add(ActionMessages.GLOBAL_MESSAGE, new ActionMessage(
                    Global.MESSAGE_SYSTEMERROR_KEY));
            this.saveMessages(request, messages);
            return mapping.findForward(Global.FORWARD_INCLUDE_MESSAGE_KEY);
        }
        return mapping.findForward("list");
    }

    
