    private ValueList getValueList(HttpServletRequest request, LPInfoForm myForm, String entryKey) {
        Map param = new HashMap();

        String lp_no = myForm.getLpNo();
        if (lp_no != null )
        	param.put("lp_no", lp_no);
        String idf_cust_no = myForm.getCustNo();
        if (idf_cust_no != null )
        	param.put("idf_cust_no", idf_cust_no);

        
        ValueList valueList = valueListService.getValueList(request, entryKey, param);
        return valueList;
    }    
