	public String getAllEmployeesJson(){
		
		List<Employee> list = EmployeeService.getInstance().getActiveEmployees();
		List<Map<String, String>> retList = new ArrayList<Map<String, String>>();
		for(Employee employee : list){
			Map<String, String> insideMap = new HashMap<String, String>();
			insideMap.put("empNo", employee.getEmpNo());
			insideMap.put("userId", employee.getUserId());
			if(employee.getCode() != null)
			   insideMap.put("codeDept", employee.getCode().getId() + "");
			else
			   insideMap.put("codeDept", "");
			retList.add(insideMap);
		}
		if(list != null && list.size() > 0)
			return JSONArray.fromObject(retList).toString();
		else
			return "";
					
	}
	

    
